sqls.sql:
select * from ( select CASE WHEN (scl.maintain_time is null) THEN i.create_time ELSE scl.maintain_time END AS FINAL_DATE, i.create_time, scl.maintain_time, i.goods_id, i.goods_code, g.generic_name, g.name, ( SELECT NAME FROM SAAS_TEST.saas_dictionary dd WHERE dd.`category` = "preparation_id" AND dd.`value` = g.preparation_id ) AS preparation_id, ( SELECT NAME FROM SAAS_TEST.saas_dictionary dd WHERE dd.`category` = "unit_id" AND dd.`value` = g.unit_id ) AS unit_id, g.specification, g.manufacturer, g.place, g.registration_code, g.pinyin_code, i.batch_code, i.order_price, i.qualified, i.supplier_id, i.supplier_code, i.storage_id, ( SELECT NAME FROM SAAS_TEST.saas_storage dd WHERE dd.id = i.storage_id ) AS storage_name, b.production_time, b.expire_time, sum(i.quantity) quantity, g.curing, CASE g.curing WHEN '0' THEN '常规' WHEN '1' THEN '重点' ELSE '其他' END AS curingName from SAAS_TEST.saas_inventory i LEFT JOIN SAAS_TEST.saas_goods g ON g.id = i.goods_id left join SAAS_TEST.saas_goods_batch b ON b.goods_id = i.goods_id and b.code=i.batch_code left join (select cl.batch_code,cl.goods_id,cl.supplier_id,cl.storage_id,max(cl.maintain_time) as maintain_time from SAAS_TEST.saas_maintain_log cl group by cl.batch_code,cl.goods_id,cl.supplier_id,cl.storage_id) scl on scl.batch_code=i.batch_code and scl.goods_id=i.goods_id and scl.supplier_id=i.supplier_id and scl.storage_id=i.storage_id where qualified=0 group by i.goods_id ,i.batch_code ,i.storage_id ,i.supplier_id) eq WHERE quantity > 0 and storage_id = 1 and ((DATE_ADD(FINAL_DATE,INTERVAL(30) DAY) < NOW() and curing=1) or (DATE_ADD(FINAL_DATE,INTERVAL(90) DAY) < NOW() and curing=0))
and goods_code = ${goods_code}
and batch_code = ${batch_code}
and NAME like %${name}%
and pinyin_code like %${pinyin_code}%
and curing = ${curing}
