SELECT id, code, supplier_id, supplier_code, supplier_name, purchase_order_id, purchase_order_code, accepter_id, accepter_code, accepter_name, accept_time, creater_id, creater_name, originator_id, originator_name, originate_time, modifier_id, modifier_name, storage_user_id, storage_user_name, storage_time, approver_id, approver_name, approve_time, pay_status, ( SELECT NAME FROM SAAS_TEST.saas_dictionary dd WHERE dd.`category` = "purchase_acceptance_status" AND dd.`value` = pay_status ) AS status_name, finance_approve_status, amount, create_time, update_time FROM SAAS_TEST.saas_acceptance where 1=1
and supplier_name like %${supplier_name}%
and supplier_code like %${supplier_code}%
and code like %${code}%
and originate_time >= ${startTime}
and originate_time <= ${endTime}
and pay_status = ${pay_status}
and originator_name like %${originator_name}%
and approver_name like %${approver_name}%
and accept_time >= ${startTime2}
and accept_time <= ${endTime2}
order by create_time desc

