sqls.sql:
SELECT id, code, supplier_id, supplier_code, supplier_name, purchaser_id, purchaser_code, purchaser_name, status, order_type, ( SELECT NAME FROM SAAS_TEST.saas_dictionary dd WHERE dd.`category` = "purchase_order_type" AND dd.`value` = order_type ) AS order_name,
case order_type when '0' then 'true' else 'false' end as show_scattered,
case mph_order_code when '0' then '' else mph_order_code end as mph_order_code,
( SELECT NAME FROM SAAS_TEST.saas_dictionary dd WHERE dd.`category` = "purchase_order_states" AND dd.`value` = status ) AS status_name, creater_id, creater_code, creater_name, approver_id, approver_code, approver_name, purchase_time, finish_time, appoint_time, remark, modifier_id, modifier_code, modifier_name, amount, create_time, update_time FROM SAAS_TEST.saas_purchase_order where 1=1
and purchase_time >= ${startTime}
and date_format(purchase_time,'%Y-%m-%d') <= ${endTime}
and supplier_name like %${supplier_name}%
and supplier_code like %${supplier_code}%
and status = ${status}
and order_type = ${order_type}
and code like %${code}%
order by create_time desc
