SELECT id, pinyin_code, CODE, NAME, supplier_nature, ( SELECT NAME FROM SAAS_TEST.saas_dictionary dd WHERE dd.`category` = "supplier_nature" AND dd.`value` = supplier_nature ) AS supplier_nature_name, registered_capital, legal_person, quality_head, address, area_code, tel, fax, email, business_person, business_person_tel, business_person_email, business_person_fax, business_pattern, business_variety, settlement_pattern, business_scope, bank_code, bank_name, bank_account, tax_registration_code, oraganization_code, oraganization_code_expire_date, business_license_code, business_license_expire_date, license_code, license_expire_date, gsp_code, gsp_expire_date, quality_deal, quality_deal_expire_date, medical_licence, medical_licence_expire_date, food_circulate, food_circulate_expire_date, health_care_licence, health_care__expire_date, cosmetics_licence, cosmetics_expire_date, cosmetics_health_code, cosmetics_health_expire_date, health_license, health_license_expire_date, annual_survey_prove, ( SELECT NAME FROM SAAS_TEST.saas_dictionary dd WHERE dd.`category` = "have_not" AND dd.`value` = supplier_nature ) AS annual_survey_prove_name, about_seal, ( SELECT NAME FROM SAAS_TEST.saas_dictionary dd WHERE dd.`category` = "have_not" AND dd.`value` = about_seal ) AS about_seal_name, peer_code, ( SELECT NAME FROM SAAS_TEST.saas_dictionary dd WHERE dd.`category` = "have_not" AND dd.`value` = peer_code ) AS peer_code_name, FIRST, ( SELECT NAME FROM SAAS_TEST.saas_dictionary dd WHERE dd.`category` = "has_seal" AND dd.`value` = FIRST ) AS FIRST_name, saas_supplier_status, CASE saas_supplier_status WHEN '0' THEN '停用' WHEN '1' THEN '启用' END AS saas_supplier_status_name, proposer, proposer_time, quality_manage_evaluation, drug_business_person, drug_business_person_advice, quality_manager_approver_id, quality_manager_approver_name, quality_manager_approval_status, CASE quality_manager_approval_status WHEN '0' THEN '未审核' WHEN '1' THEN '已审核' WHEN '2' THEN '审核驳回' END AS quality_manager_approval_status_name, quality_manager_approval_time, quality_manager_approval_reason, creater_id, creater_name, create_time, remarks FROM SAAS_TEST.saas_supplier where 1=1 and FIRST= '1'
and quality_manager_approval_time >= ${startTime}
and quality_manager_approval_time <= ${endTime}
and name like %${name}%
and registration_code like %${registration_code}%
and code like %${code}%
and quality_manager_approval_status = ${quality_manager_approval_status}
and pinyin_code like %${pinyin_code}%
order by quality_manager_approval_time desc
